// src/FirstStep.js
import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import '../styles/FirstStep.css'



function FirstStep() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    age: { day: '', month: '', year: '' },
    isSubmittingOnBehalf: false,
    hasDisability: '',
    disabilityFile: null,
  });

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setFormData({ ...formData, [name]: checked });
    } else if (name.includes('age')) {
      const field = name.split('.')[1]; // Example: age.day
      setFormData({
        ...formData,
        age: { ...formData.age, [field]: value },
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, disabilityFile: e.target.files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData); // Handle form submission here
  };

  return (

    <div>

        <div>
        <div className="container">
      <Stepper currentStep={currentStep} />
      <button
        className="btn btn-warning mt-3"
        onClick={() => setCurrentStep((prev) => Math.min(prev + 1, 5))}
      >
        Next Step
      </button>
      <button
        className="btn btn-light mt-3"
        onClick={() => setCurrentStep((prev) => Math.max(prev - 1, 1))}
      >
        Previous Step
      </button>
    </div>
        </div>
    <div className="container mt-5">
      <h2 className="mb-4">1. Your Information</h2>
      <form onSubmit={handleSubmit}>
      
        <div className="row mb-3">
          <div className="col">
            <label className="labaleName">Applicant's Name <span className="cusLable">*</span> </label>
            <input
              type="text"
              name="firstName"
              className="form-control"
              placeholder="First Name"
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="col cuscol">
            <input
              type="text"
              name="lastName"
              className="form-control"
              placeholder="Last Name"
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>

       
        <div className="form-check mb-3">
          <input
            type="checkbox"
            name="isSubmittingOnBehalf"
            className="form-check-input"
            checked={formData.isSubmittingOnBehalf}
            onChange={handleInputChange}
          />
          <label className="form-check-label">
            Are you submitting this application on behalf of someone else?
          </label>
        </div>

       
        <div className="mb-3">
          <label className="labaleName">Age <span className="cusLable">*</span></label>
          <div className="d-flex">
            <input
              type="text"
              name="age.month"
              className="form-control me-2"
              placeholder="MM"
              value={formData.age.month}
              onChange={handleInputChange}
              required
            />
            <input
              type="text"
              name="age.day"
              className="form-control me-2"
              placeholder="DD"
              value={formData.age.day}
              onChange={handleInputChange}
              required
            />
            <input
              type="text"
              name="age.year"
              className="form-control"
              placeholder="YYYY"
              value={formData.age.year}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>

       
        <div className="mb-3">
            <div>
          <label className="labaleName">Do you have a disability? <span className="cusLable">*</span></label>
          </div>
          <div className="form-check cusinput">
            <input
              type="radio"
              name="hasDisability"
              className="form-check-input"
              value="Yes"
              onChange={handleInputChange}
              checked={formData.hasDisability === 'Yes'}
              required
            />
            <label className="form-check-label">Yes</label>
          </div>
          <div className="form-check  cusinput">

            <input
              type="radio"
              name="hasDisability"
              className="form-check-input"
              value="No"
              onChange={handleInputChange}
              checked={formData.hasDisability === 'No'}
              required
            />
            <label className="form-check-label">No</label>
          </div>
        </div>

 
        {formData.hasDisability === 'Yes' && (
          <div className="mb-3">
          <label className="cusphototxt">If yes, please upload documentation of your disability:</label>
          <div className="input-group">
            <input
              type="file"
              id="fileInput"
              className="form-control-file"
              style={{ display: 'none' }}
              onChange={handleFileChange}
            />
            <label htmlFor="fileInput" className="btn btn-secondary ">
              Choose File
            </label>
            {formData.disabilityFile && (
              <span className="ms-2">{formData.disabilityFile.name}</span> 
            )}
            <button type="button" className="btn btn-secondary cusbtn">
                Upload
              </button>
          </div>
        </div>
        
        )}

        {/* Navigation Buttons */}
        <div className="d-flex justify-content-between">
          <button type="button" className="btn  backbtn">
            Back
          </button>
          <button type="submit" className="btn NextStepBtn">
            Next Step
          </button>
        </div>
      </form>
    </div>
    </div>
  );
}

export default FirstStep;
