// src/SecondStep.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Stepper from '../components/Stepper';

function SecondStep({ formData, onSave }) {
  const [additionalData, setAdditionalData] = useState({
    additionalField1: '',
    additionalField2: '',
    // Add more fields as necessary
  });

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAdditionalData({ ...additionalData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const completeData = { ...formData, ...additionalData }; // Merge form data
    console.log(completeData); // Log complete data
    onSave(completeData); // Call save function
    // Navigate to next step or success page
  };

  return (
    <div className="container mt-5">
      <Stepper currentStep={2} />
      <h2 className="mb-4">2. Additional Information</h2>
      <form onSubmit={handleSubmit}>
        {/* Additional fields */}
        <div className="mb-3">
          <label className="form-label">Additional Field 1</label>
          <input
            type="text"
            name="additionalField1"
            className="form-control"
            value={additionalData.additionalField1}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Additional Field 2</label>
          <input
            type="text"
            name="additionalField2"
            className="form-control"
            value={additionalData.additionalField2}
            onChange={handleInputChange}
            required
          />
        </div>
        {/* Submit button */}
         {/* Navigation Buttons */}
         <div className="d-flex justify-content-between">
          <button
            type="button"
            className="btn backbtn"
           
          >
            Back
          </button>
          <button
            type="button"
            className="btn NextStepBtn"
            // onClick={goToNextStep} // Navigate to the second step
          >
            Next Step
          </button>
        </div>
      </form>
    </div>
  );
}

export default SecondStep;
