// src/FourthStep.js
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Stepper from '../components/Stepper';
import { useLocation } from 'react-router-dom';
import { useFormContext } from '../FormContext'; // Import the custom hook

function FourthStep() {
    const { formData } = useFormContext(); // Access the context

    const {
        firstName = '',
        lastName = '',
        age = { month: '', day: '', year: '' },
        hasDisability = 'No',
        disabilityFile = null,
        mealPlanChoice = '',
        daysRequested = [],
        deliveryAddress = '',
        deliveryInstructions = '',
        phoneCountryCode = '',
        phoneAreaCode = '',
        phoneNumber = '',
        email = '',
    } = formData;

    return (
        <div className="container mt-5">
            <Stepper currentStep={4} />

            <h2 className="mb-4">4. Review and Confirm</h2>

            {/* Review information fields here */}
        </div>
    );
}

export default FourthStep;
