// src/pages/FirstStep.js
import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/FirstStep.css';
import Stepper from '../components/Stepper';
import { FormContext } from '../context/FormContext';

function FirstStep() {
  const { formData, updateFormData } = useContext(FormContext); // Access form context
  const [currentStep, setCurrentStep] = useState(1); // Step tracker
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : value;

    if (name.includes('age')) {
      const field = name.split('.')[1]; // Extract 'day', 'month', or 'year'
      updateFormData('age', { ...formData.age, [field]: newValue });
    } else {
      updateFormData(name, newValue); // Update other fields
    }
  };

  const handleFileChange = (e) => {
    updateFormData('disabilityFile', e.target.files[0]); // File upload
  };

  const goToNextStep = () => {
    setCurrentStep((prev) => Math.min(prev + 1, 6)); // Move to next step
    console.log(formData);
    navigate('/second-step'); // Navigate to the next step
  };

  const goToPreviousStep = () => {
    setCurrentStep((prev) => Math.max(prev - 1, 1)); // Move back a step
  };

  return (
    <div className="container mt-5">
      <Stepper currentStep={currentStep} />

      <h2 className="mb-4">1. Your Information</h2>
      <form>
        {/* Applicant's Name */}
        <div className="row mb-3">
          <div className="col">
            <label className="labaleName">
              Applicant's Name <span className="cusLable">*</span>
            </label>
            <input
              type="text"
              name="firstName"
              className="form-control"
              placeholder="First Name"
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="col cuscol">
            <input
              type="text"
              name="lastName"
              className="form-control"
              placeholder="Last Name"
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>

        {/* Submitting on Behalf Checkbox */}
        <div className="form-check mb-3">
          <input
            type="checkbox"
            name="isSubmittingOnBehalf"
            className="form-check-input"
            checked={formData.isSubmittingOnBehalf}
            onChange={handleInputChange}
          />
          <label className="form-check-label">
            Are you submitting this application on behalf of someone else?
          </label>
        </div>

        {/* Additional Information if Submitting on Behalf */}
        {formData.isSubmittingOnBehalf && (
          <>
            <div className="mb-3">
              <input
                type="text"
                name="submittingOnBehalfName"
                className="form-control additionalCheckFirst"
                placeholder="First Name"
                value={formData.submittingOnBehalfName}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="mb-3">
              <input
                type="text"
                name="submittingOnBehalfRelation"
                className="form-control additionalCheckSecond"
                placeholder="Last Name"
                value={formData.submittingOnBehalfRelation}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="mb-3 cuschekboxFirst">
              <label className="form-label">Relationship to applicant</label>
              <select
                name="submittingOnBehalfReason"
                className="form-control"
                value={formData.submittingOnBehalfReason}
                onChange={handleInputChange}
                required
              >
                <option value="">Select Reason</option>
                <option value="Medical">Medical</option>
                <option value="Legal">Legal</option>
                <option value="Other">Other</option>
              </select>
            </div>
          </>
        )}

        {/* Age Input */}
        <div className="mb-3">
          <label className="labaleName">Age <span className="cusLable">*</span></label>
          <div className="d-flex">
            <input
              type="text"
              name="age.month"
              className="form-control me-2"
              placeholder="MM"
              value={formData.age.month}
              onChange={handleInputChange}
              required
            />
            <input
              type="text"
              name="age.day"
              className="form-control me-2"
              placeholder="DD"
              value={formData.age.day}
              onChange={handleInputChange}
              required
            />
            <input
              type="text"
              name="age.year"
              className="form-control"
              placeholder="YYYY"
              value={formData.age.year}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>

        {/* Disability Status */}
        <div className="mb-3">
          <label className="labaleName">Do you have a disability? <span className="cusLable">*</span></label>
          <div className="form-check">
            <input
              type="radio"
              name="hasDisability"
              className="form-check-input"
              value="Yes"
              onChange={handleInputChange}
              checked={formData.hasDisability === 'Yes'}
              required
            />
            <label className="form-check-label">Yes</label>
          </div>
          <div className="form-check">
            <input
              type="radio"
              name="hasDisability"
              className="form-check-input"
              value="No"
              onChange={handleInputChange}
              checked={formData.hasDisability === 'No'}
              required
            />
            <label className="form-check-label">No</label>
          </div>
        </div>

        {/* File Upload Section */}
        {formData.hasDisability === 'Yes' && (
          <div className="mb-3">
            <label className="cusphototxt">Upload documentation of your disability:</label>
            <input type="file" className="form-control" onChange={handleFileChange} />
            {formData.disabilityFile && <span>{formData.disabilityFile.name}</span>}
          </div>
        )}

        {/* Navigation Buttons */}
        <div className="d-flex justify-content-between">
          <button type="button" className="btn backbtn" onClick={goToPreviousStep}>
            Back
          </button>
          <button type="button" className="btn NextStepBtn" onClick={goToNextStep}>
            Next Step
          </button>
        </div>
      </form>
    </div>
  );
}

export default FirstStep;
